#!/usr/bin/env python3

DNA = ''

DNA_upper = DNA.upper()

print('The number of A\'s:', DNA.count('A'))
print('The number of T\'s:', DNA.count('T'))
print('The number of C\'s:', DNA.count('C'))
print('The number of G\'s:', DNA.count('G'), '\n')

RNA = DNA_upper.replace('T', 'U')
print('RNA:', RNA, '\n')

DNA2 = ''

DNA2_upper = DNA2.upper()
RNA2 = DNA2_upper.replace('T', 'U')

print('The number of A\'s in RNA2:', RNA2.count('A'))
print('The number of U\'s in RNA2:', RNA2.count('U'))
print('The number of C\'s in RNA2:', RNA2.count('C'))
print('The number of G\'s in RNA2:', RNA2.count('G'), '\n')

DNA3 = ''

AT_count = DNA.count('A') + DNA.count('T')
total_count = DNA.count('A') + DNA.count('T') + DNA.count('G') + DNA.count ('C')
print('AT count:', AT_count)
print('Total nt:', total_count)

AT_content = AT_count/total_count
print('{:.2%}'.format(AT_content), '\n')

