#!/usr/bin/env python3
import re
import sys


def split_by_length(x, y):
    x = x.replace("\n","")
    regex = "(.{1,"+ str(y) + "})" 
    line = re.findall(regex, x)
    seq = '\n'.join(line)
    return seq


